        public int Map_Width
        {
            get { return gameObjectsStore.tileMapOrigin.GetLength(1); }
        }

        public int Map_Height
        {
            get { return gameObjectsStore.tileMapOrigin.GetLength(0); }
        }

        public TileType GetCollision(int x, int y)
        {
            if (x < 0 || x >= gameObjectsStore.tileMap.GetLength(0))
            {
                return TileType.Impassable;
            }
            if (y < 0 || y >= gameObjectsStore.tileMap.GetLength(1))
            {
                return TileType.Passable;
            }

            return gameObjectsStore.tileMap[x, y].tileType;
        }

        public TileAction GetCrawl(int x, int y)
        {
            if (x < 0 || x >= gameObjectsStore.tileMap.GetLength(0))
            {
                return TileAction.None;
            }
            if (y < 0 || y >= gameObjectsStore.tileMap.GetLength(1))
            {
                return TileAction.None;
            }
            return gameObjectsStore.tileMap[x, y].tileAction;
        }

        public Rectangle GetBounds(int x, int y)
        {
            return new Rectangle(x * (Tiles.Width), y * (Tiles.Height), (Tiles.Width), (Tiles.Height));
        }

